mapper.select:
select * from ( select to_char(reg_date, 'yyyy-mm') as day from account_book
and to_char(reg_date, 'yyyy-mm') = #{date}
and to_char(reg_date, 'yyyy-mm') = to_char((sysdate), 'yyyy-mm')
) group by day

		select
        *
    	from (
	        select
	            nvl(income_expense, 0) as income_expense,
	            sum(price) as total
	         from (
	                select
	                    *
	                from
	                    account_book

	                    id = #{id}


		                    and
		                    to_char(reg_date, 'yyyy-mm') = #{date}


		                    and
		                    to_char(reg_date, 'yyyy-mm') = to_char((sysdate), 'yyyy-mm')



          		)
	    group by income_expense
	    )

select sum(plus_minus) from ( select case when income_expense = 'E' then price * -1 when income_expense ='I' then price * 1 end as plus_minus from ( select * from account_book
id = #{id}
and to_char(reg_date, 'yyyy-mm') = #{date}
and to_char(reg_date, 'yyyy-mm') = to_char((sysdate), 'yyyy-mm')
order by reg_date desc ) )

select * from account_book where id = #{id}
and income_expense = #{incomeExpense}
order by reg_date desc

select * from account_book
id = #{id}
and category=#{category}
and income_expense=#{incomeExpense}
and detail like '%' || #{detail} || '%'
order by reg_date desc

select sum(price) as total, category from (select * from account_book
id = #{id}
and to_char(reg_date, 'yyyy-mm') = #{date}
and to_char(reg_date, 'yyyy-mm') = to_char((sysdate), 'yyyy-mm')
and income_expense = #{incomeExpense}
) group by category

select 
	count(*) 
from 
	account_book 
where id=#{id}

	and 
	income_expense = #{incomeExpense}


	and 
	category = #{category}


	and 
	detail = #{detail}

select sum(price) as sum, count(*) as count from ( select * from account_book
id = #{id}
and to_char(reg_date, 'yyyy-mm') = #{date}
and to_char(reg_date, 'yyyy-mm') = to_char((sysdate), 'yyyy-mm')
)

select * from (select category, count(category) as count, sum(price) as sum from (select to_char(reg_date, 'yyyy-mm') as monthly, category, price from account_book
id = #{id}
and to_char(reg_date, 'yyyy-mm') = #{date}
and to_char(reg_date, 'yyyy-mm') = to_char((sysdate), 'yyyy-mm')
and income_expense= #{incomeExpense}
) group by monthly, category) where rownum <= 3 order by sum desc

		select
		  nvl(payment, 'blank') as payment,
		  count(payment) as count
		 from ( 
		select  
		    payment
		from
		    account_book

             id = #{id}


              and
              to_char(reg_date, 'yyyy-mm') = #{date}


              and
              to_char(reg_date, 'yyyy-mm') = to_char((sysdate), 'yyyy-mm')


		    and
		    income_expense = #{incomeExpense}

		) group by payment
