mapper.update:
update member set 

	profile = #{profile}, profile_status = 'Y' where id = #{id}		


	profile = null, profile_status = 'N' where id = #{id}

mapper.select:
select 
	* 
from 
	likes 

	id = #{id}
	and
	code = #{code}
	or code = #{aCode}

mapper.update:
